body {
  font-family: system-ui, "Helvetica Neue", "Arial", sans-serif;
  font-size: 16px;
  color: #333;
  background-color: #fefefe;
  margin: 0;
  padding: 1rem;
  -webkit-font-smoothing: antialiased;
  display: flex;
  flex-direction: column;
  overflow-x: hidden;
}

footer {
  position: fixed;
  bottom: 0;
  width: 100%;
  display: flex;
  justify-content: flex-end;
}
footer a {
  opacity: 0;
  text-decoration: none;
  text-transform: uppercase;
  font-size: 0.75rem;
  padding: 1rem;
  transform: translateX(-1rem);
  transition: all 500ms ease-in-out;
}
footer a #shuffle {
  width: 1.25rem;
  height: 1.25rem;
  transition: all 500ms ease-in-out 150ms;
}
footer a:hover {
  opacity: 1;
}
footer a:hover #shuffle {
  transform: rotate(-75deg);
}
footer .fade-in {
  opacity: 1;
}

nav a {
  text-decoration: none;
}

ul {
  padding: 0.25rem 0;
  margin: 0 0 1rem 0;
  list-style-type: none;
  display: flex;
  justify-content: space-between;
  border-bottom: 2px solid;
}

::-moz-selection {
  background: #eee;
}

::selection {
  background: #eee;
}

a {
  color: inherit;
}

.light-link--hover:hover {
  color: #d8d8d8;
}

.dark-link--hover:hover {
  color: gray;
}

.link-group {
  display: flex;
}
.link-group li + li {
  margin-left: 0.5rem;
}

.large {
  margin-bottom: 2rem;
  font-size: 1.8rem;
  font-weight: 500;
  line-height: 1;
  letter-spacing: -0.03rem;
}
.large span {
  font-size: 1.25rem;
}

p {
  font-size: 1.25rem;
  font-weight: 400;
  letter-spacing: -0.05rem;
  line-height: 1.25;
  font-family: "Spectral", serif;
}

@media only screen and (min-width: 480px) {
  nav {
    font-size: 1rem;
  }

  .link-group li + li {
    margin-left: 1rem;
  }

  .large {
    font-size: 6vw;
    font-weight: 500;
  }
  .large span {
    font-size: 4vw;
  }
}
@media only screen and (min-width: 768px) {
  .large {
    margin-bottom: 3vw;
  }

  p {
    font-size: 1.5rem;
    max-width: 768px;
  }
}
